import { useRef, useState, useEffect } from 'react';
import Input from '../../UI/Input';
import classes from './MealItemForm.module.css';

const MealItemForm = (props) => {
  const [amountIsValid, setAmountIsValid] = useState(true);
  const amountInputRef = useRef();

  // Retrieve items from local storage on component mount
  useEffect(() => {
    const storedItems = JSON.parse(localStorage.getItem('items')) || [];
    console.log(storedItems);
  }, []);

  const submitHandler = (event) => {
    event.preventDefault();

    const enteredAmount = amountInputRef.current.value;
    const enteredAmountNumber = +enteredAmount;

    if (
      enteredAmount.trim().length === 0 ||
      enteredAmountNumber < 1 ||
      enteredAmountNumber > 5
    ) {
      setAmountIsValid(false);
      return;
    }

    // Update local storage with the new item
    const newItem = {
      id: props.id,
      amount: enteredAmountNumber,
    };

    // Retrieve existing items from local storage
    const storedItems = JSON.parse(localStorage.getItem('items')) || [];

    // Update the stored items array with the new item
    const updatedItems = [...storedItems, newItem];

    // Save the updated items back to local storage
    localStorage.setItem('items', JSON.stringify(updatedItems));

    // Clear the input field and reset validation
    amountInputRef.current.value = '1';
    setAmountIsValid(true);
  };

  return (
    <form className={classes.form} onSubmit={submitHandler}>
      <Input
        ref={amountInputRef}
        label='Amount'
        input={{
          id: 'amount_' + props.id,
          type: 'number',
          min: '1',
          max: '5',
          step: '1',
          defaultValue: '1',
        }}
      />
      <button>+ Add</button>
      {!amountIsValid && <p>Please enter a valid amount (1-5).</p>}
    </form>
  );
};

export default MealItemForm;
